import logo from "./logo.svg";
import "./App.css";
import Hello from "./Hello";

function App() {
  const name = "Jeeyoung";
  const style = {
    backgroundColor: "black",
    color: "aqua",
    fontSize: 24,
    padding: "1rem",
  };

  return (
    <>
      {/* 주석은 다음과 같이 */}
      /* 이러면 보여요 */
      <Hello
        name="react"
        color="red"
        // 열리는 태그 내부에서는
        // 이런 형태의 주석도 가능합니다.
      />
      <Hello color="pink" />
      <div style={style}>{name}</div>
      <div className="gray-box"></div>
    </>
  );
}

export default App;
