import React from "react";

function Hello({ color, name, isSpecial }) {
  return (
    <div
      style={{
        color,
      }}
    >
      {isSpecial ? <b>*</b> : null}
      Hello, {name}
    </div>
  );
}

Hello.defaultProps = {
  name: "No name",
};

// 리액트 컴포넌트 파일에서 XML 형태로 코드를 작성하면,
// babel이 JSX를 JavaScript로 변환해 준다.

export default Hello;
